  color: #907e82;
  text-decoration: none;
}
@media screen and (min-width: 768px) {
  .contacts-item {
    font-size: 14px;
    line-height: 1.86;
  }
}
@media screen and (min-width: 1280px) {
  .contacts-item {
    font-size: 16px;
    line-height: 1.8;
  }
}

.outlets-time-item:first-child {
  margin-bottom: 10px;
}
@media screen and (min-width: 768px) {
  .outlets-time-item:first-child {
    margin-bottom: 15px;
  }
}

.outlets-nav {
  display: flex;
  justify-content: center;
}
.outlets-nav:not(:last-child) {
  margin-right: 15px;
}
@media screen and (max-width: 767px) {
  .outlets-nav {
    flex-direction: column;
    align-items: center;
  }
}

.outlets-nav .btn {
  padding: 0;
  padding-top: 12px;
  padding-bottom: 12px;
  display: block;
  font-weight: 700;
  font-size: 16px;
  line-height: 1.3125;
  color: #d41443;
  background-color: #ffffff;
  border: 0;
  border-radius: 22px;
  transition: background-color 250ms cubic-bezier(0.4, 0, 0.2, 1);
}
.outlets-nav .btn:hover, .outlets-nav .btn:focus {
  background-color: #ecebeb;
}

.outlets-red {
  margin-bottom: 10px;
  max-width: 161px;
  width: 100%;
}
@media screen and (min-width: 768px) {
  .outlets-red {
    margin: 0 15px 0 0;
    max-width: 191px;
    width: 100%;
  }
}

.outlets-white {
  width: 100%;
  max-width: 161px;
}
@media screen and (min-width: 768px) {
  .outlets-white {
    max-width: 174px;
    width: 100%;
  }
}

button {
  outline: transparent;
}

.btn.outlets-red {
  background-color: #d41443;
  color: #ffffff;
}

.outlets-btn-icon {
  margin-left: 8px;
  width: 7px;
  height: 10px;
  stroke: #d41443;
}

.outlets-btn-icon-white {
  margin-left: 8px;
  width: 7px;
  height: 10px;
  stroke: #ffffff;
}

.footer {
  background-color: #fff5f6;
}

.footer-content {
  border-bottom: 1px solid #e5e5e5;
}
@media screen and (min-width: 768px) {
  .footer-content {
    display: flex;
    padding-bottom: 91px;
  }
}
@media screen and (max-width: 767px) {
  .footer-content {
    padding-bottom: 97px;
  }
}
@media screen and (min-width: 1280px) {
  .footer-content {
    display: flex;
    justify-content: space-between;
  }
}

@media screen and (max-width: 767px) {
  .footer-text {
    width: 234px;
    margin-bottom: 43px;
  }
}
@media screen and (min-width: 768px) {
  .footer-text {
    margin-right: 92px;
    width: 340px;
  }
}
@media screen and (min-width: 1280px) {
  .footer-text {
    margin-right: 257px;
    width: 450px;
  }
}

.toptext {
  font-family: Titan One;
  font-style: normal;
  font-weight: normal;
  font-size: 18px;
  line-height: 1.166;
  text-transform: uppercase;
  color: #ffa5ba;
}
@media screen and (max-width: 767px) {
  .toptext {
    margin-bottom: 17px;
  }
}
@media screen and (min-width: 768px) {
  .toptext {
    margin-bottom: 15px;
    font-size: 26px;
    line-height: 1.153;
  }
}
@media screen and (min-width: 1280px) {
  .toptext {
    margin-bottom: 15px;
    font-size: 28px;
    line-height: 1.133;
  }
}

.lowertext {
  font-family: Titan One;
  font-style: normal;
  font-weight: normal;
  font-size: 34px;
  line-height: 1.147;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  color: #d41443;
}
@media screen and (min-width: 768px) {
  .lowertext {
    font-size: 48px;
    line-height: 1.145;
  }
}
@media screen and (min-width: 1280px) {
  .lowertext {
    font-size: 54px;
    line-height: 1.137;
  }
}

@media screen and (max-width: 767px) {
  .footer-contacts {
    padding-bottom: 43px;
  }
}

.contacts-icon__list {
  display: flex;
  justify-content: flex-end;
  margin-bottom: 46px;
}

.contacts-icon__item {
  margin-right: 28px;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 50%;
  width: 32px;
  height: 32px;
  background-color: #ffa5ba;
  transition: all 250ms ease;
}
.contacts-icon__item:last-child {
  margin-right: 0;
}
.contacts-icon__item:hover {
  background-color: #d41443;
}

.icon-link {
  fill: #ffffff;
  padding-top: 2px;
}
.icon-link_telegram {
  margin-left: -3px;
}

.contacts-tel {
  font-family: DM Sans;
  font-style: normal;
  font-weight: bold;
  font-size: 12px;
  line-height: 1.86;
  text-align: right;
}

.tel-number {
  color: #d41443;
  text-decoration: none;
  margin-left: 1px;
}

.copyright {
  text-align: center;
}

.copyright-text {
  padding-top: 22px;
  padding-bottom: 23px;
  font-family: DM Sans;
  font-style: normal;
  font-weight: bold;
  font-size: 14px;
  line-height: 1.52;
  color: #907e82;
}
@media screen and (max-width: 767px) {
  .copyright-text {
    white-space: pre-line;
  }
}
@media screen and (min-width: 768px) {
  .copyright-text {
    padding-top: 29px;
    padding-bottom: 18px;
  }
}
@media screen and (min-width: 1280px) {
  .copyright-text {
    padding-top: 9px;
    padding-bottom: 10px;
  }
}

