sources.sass\\utils\\_variables.scss:
/*=== fonts ===*/
$main-text-font: DM Sans;
$title-font: Titan One;

/*=== colors ===*/
// --text-colors
$first-text-color: #000000;
$second-text-color: #907e82;
$third-text-color: #ffffff;
$accent-color: #d41443;

// title-colors
$first-title-color: #d41443;
$second-title-color: #ffa5ba;

// background-colors
$header-bg-color: #ffb8ca;
$main-bg-color: #e5e5e5;
$second-bg-color: #fff5f6;
$slider-bg-color: #fafafa;
$mobile-menu-bg-color: #e17992;

// button-colors
$button-bg-1: #ffffff;
$button-bg-2: #d41443;
$button-hover-1: #ecebeb;
$button-hover-2: #c7133e;

// contant-containers-colors
$product-contant-container-1: #ffb8ca;
$product-contant-container-2: #f0d1a5;
$product-contant-container-3: #c2e297;

sources.sass\\base\\_common.scss:
body {
  font-family: $main-text-font, sans-serif;
  font-style: normal;
  font-weight: 500;
  font-size: 14px;
  color: $first-text-color;
}
ul {
  padding: 0;
  margin: 0;
  list-style: none;
}

img {
  display: block;
  max-width: 100%;
  height: auto;
}
h1,
h2,
h3,
h4,
h5,
h6 {
  margin: 0;
  font-family: $title-font, sans-serif;
  font-style: normal;
  font-weight: 400;
  font-size: 20px;
  color: $first-text-color;
}
p {
  margin: 0;
}
.content-container {
  width: 320px;
  padding-left: 20px;
  padding-right: 20px;
  margin-left: auto;
  margin-right: auto;
  @media screen and(min-width:768px) {
    width: 768px;
    padding-left: 34px;
    padding-right: 34px;
  }
  @media screen and(min-width:1280px) {
    width: 1280px;
    padding-left: 115px;
    padding-right: 115px;
  }
}

sources.sass\\components\\_header.scss:
// стиль для ссылок
.link {
  text-decoration: none;
  display: block;
}

.content-container-overlay {
  padding-left: 20px;
  padding-right: 20px;
  margin-left: auto;
  margin-right: auto;

  @media screen and (max-width: 408px) {
    min-width: 320px;
  }

  @media screen and (min-width: 409px) and (max-width: 767px) {
    width: 408px;
  }

  @media screen and(min-width:768px) {
    width: 768px;
    padding-left: 34px;
    padding-right: 34px;
  }

  @media screen and(min-width:1280px) {
    width: 1280px;
    padding-left: 115px;
    padding-right: 115px;
  }
}

.overlay {
  position: relative;

  padding-top: 6px;
  height: 502px;
  overflow-x: hidden;

  @media screen and (min-width: 768px) {
    height: 400px;
    overflow-x: unset;
    padding-top: 37px;
    padding-bottom: 28px;
  }
  @media screen and (min-width: 1280px) {
    height: 665px;
    overflow-x: unset;
    padding-top: 38px;
    padding-right: 46px;
    padding-left: 46px;
  }
}

.header {
  // Обертка для хедера
  background-color: $header-bg-color;

  &-ice-cream {
    overflow-x: visible;

    &::before {
      content: '';
      background-color: $second-title-color;
      width: 346px;
      height: 344px;
      position: absolute;
      border-radius: 50%;

      @media screen and (max-width: 767px) {
        top: 83px;
        left: 36px;
        overflow-x: visible;
      }

      @media screen and (min-width: 768px) {
        width: 324px;
        height: 322px;
        top: 31px;
        right: 137px;
      }

      @media screen and (min-width: 1280px) {
        width: 538px;
        height: 538px;
        top: 52px;
        right: 267px;
      }
    }
  }

  // Кнопка купить
  &-btn {
    margin-left: auto;
    margin-right: auto;
    margin-bottom: 20px;
    padding: 12px 0px;
    display: block;
    width: 210px;

    font-weight: 700;
    font-size: 16px;
    line-height: 1.31;

    color: $button-bg-2;
    background-color: $button-bg-1;
    border: 0;
    border-radius: 22px;
    transition: background-color 250ms cubic-bezier(0.4, 0, 0.2, 1);
  }

  &-btn:hover,
  &-btn:focus {
    background-color: $button-hover-1;
  }

  &-btn-icon {
    margin-left: 25px;
    width: 7px;
    height: 10px;
    stroke: $button-bg-2;
  }
}
.main-menu {
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
}

.main-nav {
  @media screen and (max-width: 1279px) {
    display: none;
  }
  z-index: 5;
  &-list {
    display: flex;
  }
  &-item {
    &:not(:first-child) {
      margin-left: 35px;
    }
  }
  &-link {
    display: block;
    font-family: $main-text-font;
    font-style: normal;
    font-weight: 700;
    font-size: 16px;
    line-height: 1.31;

    display: flex;
    align-items: center;

    color: $third-text-color;

    &:hover,
    &:focus {
      color: $accent-color;
    }
  }
}

.mobile-menu {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 999;
  opacity: 0;
  visibility: hidden;
  pointer-events: none;
  background-color: rgba(144, 126, 130, 0.7);
  transition: opacity 250ms cubic-bezier(0.4, 0, 0.2, 1),
    visibility 250ms cubic-bezier(0.4, 0, 0.2, 1);

  &.is-open {
    opacity: 1;
    visibility: visible;
    pointer-events: initial;
  }

  &.is-open &-content {
    transform: translateX(0);
  }

  &-content {
    margin-left: auto;
    filter: drop-shadow(-8px 0px 20px rgba(0, 0, 0, 0.25));

    position: relative;
    height: 100%;
    background-color: $mobile-menu-bg-color;
    width: 254px;
    transform: translateX(100%);

    transition: transform 250ms cubic-bezier(0.4, 0, 0.2, 1);

    padding-top: 60px;
  }

  &-close {
    display: none;
  }

  &-btn.is-open &-burger {
    display: none;
  }

  &-close {
    display: block;
    stroke: $third-text-color;
    cursor: pointer;

    &:hover,
    &:focus {
      stroke: $accent-color;
    }
  }

  &-btn {
    display: block;
    position: absolute;
    top: 24px;
    right: 20px;

    background-color: transparent;
    width: auto;
    height: 20px;
    border: none;
    padding: 0;

    &.open {
      @media screen and (min-width: 768px) and (max-width: 1279px) {
        top: 52px;
        right: 224px;
        z-index: 4;
        outline: none;
      }
      @media screen and (min-width: 1280px) {
        display: none;
      }
    }
  }
  &-icon {
    display: block;
    width: 20px;
    height: 20px;

    stroke: $button-bg-2;
  }
  & .btn.header-btn {
    width: 209px;

    @media screen and (min-width: 768px) {
      width: 186px;
    }
  }
}

.site-nav {
  &-list {
    display: flex;
    flex-direction: column;
    justify-content: center;

    border-top: 1px solid #e18298;
  }

  &-item {
    display: flex;
    align-items: center;
    padding-left: 24px;
    width: 100%;
    height: 42px;
    border-bottom: 1px solid #e18298;
  }

  &-link {
    font-family: $main-text-font;
    font-style: normal;
    font-weight: 700;
    font-size: 14px;
    line-height: 1.3;

    color: $third-text-color;

    &:hover,
    &:focus {
      color: $accent-color;
    }
  }
}

.positioned-btn {
  position: relative;
  z-index: 5;
  padding: 0;
  padding-top: 12px;
  padding-bottom: 12px;
  display: block;
  width: 159px;
  text-align: start;
  padding-left: 38px;

  font-weight: 700;
  font-size: 16px;
  line-height: 1.31;
  color: $accent-color;
  background-color: $third-text-color;
  border: 0;
  border-radius: 22px;

  transition: background-color 250ms cubic-bezier(0.4, 0, 0.2, 1);

  @media screen and (max-width: 767px) {
    display: none;
  }

  @media screen and (min-width: 1280px) {
    margin-right: 50px;
  }

  cursor: pointer;
  &:hover,
  &:focus {
    background-color: $button-hover-1;
  }

  &-icon {
    width: 8px;
    height: 16px;
    position: absolute;
    top: 15px;
    right: 38px;
    stroke: $accent-color;
  }
}

// Стили блока-героя
.hero {
  padding-top: 193px;
  padding-right: 0;
  padding-left: 0;

  @media screen and (min-width: 768px) {
    padding-top: 15px;
  }

  &-milk {
    margin-top: 20px;
    position: relative;
    margin-bottom: 28px;

    @media screen and (max-width: 767px) {
      display: none;
    }
    @media screen and (min-width: 1280px) {
      display: flex;
      align-items: center;
      margin-top: 56px;
      position: absolute;
    }
  }

  @media screen and (min-width: 1280px) {
    padding-left: 50px;
    padding-right: 50px;
  }

  // Обертка для заголовка героя и кнопок под ним
  &-title-container {
    position: relative;
    z-index: 5;

    @media screen and (min-width: 1280px) {
      margin-top: 69px;
    }
  }

  // Заголовок героя
  &-title {
    margin-bottom: 20px;
    width: 230px;

    font-family: Titan One;
    font-weight: 400;
    font-size: 48px;
    line-height: 0.965;
    letter-spacing: 0.04em;

    color: $button-bg-2;
    text-transform: uppercase;

    @media screen and (min-width: 768px) {
      font-size: 34px;
      line-height: 0.905;
      width: 162px;
    }

    @media screen and (min-width: 1280px) {
      font-size: 58px;
      line-height: 0.905;
      width: 277px;
      margin-left: 23px;
    }
  }

  &-accent {
    display: block;

    font-size: 26px;
    line-height: 1.15;
    letter-spacing: 0.04em;

    color: $button-bg-1;
    @media screen and (min-width: 768px) {
      font-size: 22px;
      line-height: 25px;
    }
    @media screen and (min-width: 1280px) {
      font-size: 38px;
      line-height: 1.16;
      width: 277px;
    }
  }

  // Кнопки героя
  &-btn {
    cursor: pointer;
    &-accent {
      margin-right: 8px;
    }
    padding: 8px 13px;

    font-weight: normal;
    font-size: 8px;
    line-height: 1.25;

    color: $button-bg-2;
    background-color: $button-bg-1;
    border: 0;
    border-radius: 20px;
    transition: background-color 250ms cubic-bezier(0.4, 0, 0.2, 1);

    @media screen and (min-width: 1280px) {
      min-width: 104px;
      height: 40px;
      font-size: 14px;
      line-height: 1.23;
      padding: 8px 20px;
      margin-left: 23px;

      &-accent {
        margin-right: 15px;
      }
    }
  }
  &-btn:hover,
  &-btn:focus {
    background-color: $button-hover-1;
  }

  &-btn-accent {
    color: $button-bg-1;
    background-color: $button-bg-2;
    transition: background-color 250ms cubic-bezier(0.4, 0, 0.2, 1),
      text-shadow 250ms cubic-bezier(0.4, 0, 0.2, 1);
  }
  &-btn-accent:hover,
  &-btn-accent:focus {
    text-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    background-color: $button-hover-2;
  }
}

.statistics {
  position: absolute;
  top: 97px;
  right: 42px;

  @media screen and (max-width: 767px) {
    display: none;
  }

  @media screen and (min-width: 1280px) {
    top: 156px;
    right: 97px;
  }

  &-item {
    display: flex;
    flex-direction: column;

    margin-bottom: 13px;
    position: relative;

    &::before {
      content: '';
      width: 16px;
      height: 16px;
      border-radius: 50%;
      background-color: $third-text-color;
      position: absolute;
      top: -3px;
      right: -8px;

      @media screen and (min-width: 1280px) {
        width: 30px;
        height: 30px;
        top: -6px;
        right: -18px;
      }
    }
  }
  &-number {
    display: block;
    font-family: $title-font;
    font-style: normal;
    font-size: 28px;
    line-height: 1.13;

    text-align: right;
    letter-spacing: 0.04em;
    text-transform: uppercase;

    color: $accent-color;
    z-index: 4;

    @media screen and (min-width: 1280px) {
      font-size: 46px;
      line-height: 1.15;
    }
  }
  &-text {
    font-family: $main-text-font;
    font-style: normal;
    font-weight: normal;
    font-size: 8px;
    line-height: 1.25;
    text-align: right;

    color: $third-text-color;
    text-transform: lowercase;

    @media screen and (min-width: 1280px) {
      font-size: 14px;
      line-height: 1.3;
    }
  }
}

.div-milk {
  @media screen and (min-width: 768px) and (max-width: 1279px) {
    position: absolute;
    left: 120px;
    top: 47px;
  }

  @media screen and (min-width: 1280px) {
    position: absolute;
    left: 190px;
    top: 55px;
  }

  &-text {
    font-family: $main-text-font;
    font-style: normal;
    font-size: 8px;
    line-height: 1.5;
    color: $third-text-color;
    width: 114px;
    height: 48px;

    @media screen and (min-width: 1280px) {
      font-size: 14px;
      line-height: 1.4;
      width: 200px;
      height: 78px;
    }
  }
  &-btn {
    height: 18px;
    width: 18px;
    background-color: $third-text-color;
    border: 0;
    border-radius: 50%;
    display: block;
    margin-top: 7px;
    position: relative;

    @media screen and (min-width: 1280px) {
      margin-top: 20px;
      width: 26px;
      height: 26px;
    }

    cursor: pointer;
    &:hover,
    &:focus {
      background-color: $button-hover-1;
    }
  }
  &-btn-icon {
    position: absolute;
    top: 25%;
    left: 30%;
    fill: $accent-color;
    width: 8px;
    height: 8px;
    @media screen and (min-width: 1280px) {
      top: 30%;
      width: 11px;
      height: 11px;
    }
  }
}

// Стили картинки header-ice-cream
.img {
  &-girl {
    position: absolute;
    right: 34px;
    bottom: 0;
    z-index: 9;

    @media screen and(max-width:767px) {
      display: none;
    }

    @media screen and (min-width: 1280px) {
      width: 293px;
      right: 109px;
    }
  }

  &-ice-cream {
    position: absolute;
    bottom: 0;

    @media screen and (max-width: 767px) {
      left: 117px;
      width: 171px;
    }

    @media screen and (min-width: 768px) {
      width: 143px;
      right: 247px;
    }

    @media screen and (min-width: 1280px) {
      width: 236px;
      right: 450px;
    }
  }

  &-milk {
    @media screen and (min-width: 1280px) {
      width: 188px;
    }
  }
}

sources.sass\\components\\_products.scss:
.products {
    padding-top: 121px;
    padding-bottom: 98px;
    text-align: center;

    @media screen and (min-width: 768px) {
        padding-top: 122px;
        padding-bottom: 112px;
    }

    @media screen and (min-width: 1280px) {
        padding-top: 127px;
        padding-bottom: 119px;
    }
}

.products-title {
    margin-bottom: 144px;

    font-size: 34px;
    line-height: 1.147;
    letter-spacing: 0.04em;
    text-transform: uppercase;

    color: $first-title-color;

    @media screen and (min-width: 768px) {
        margin-bottom: 150px;

        font-size: 48px;
    }

    @media screen and (min-width: 1280px) {
        margin-bottom: 259px;

        font-size: 58px;
        line-height: 1.138;
        letter-spacing: 0.06em;
    }
}

.products-subtitle {
    margin-bottom: 15px;

    font-family: $title-font, sans-serif;
    font-weight: 400;
    font-size: 18px;
    line-height: 1.167;
    text-transform: uppercase;

    color: $second-title-color;

    @media screen and (min-width: 768px) {
        font-size: 26px;
        line-height: 1.15;
    }

    @media screen and (min-width: 1280px) {
        font-size: 30px;
        line-height: 1.133;
        letter-spacing: 0.04em;
    }
}

.products-item {
    margin-bottom: 30px;

    line-height: 1.15;
    letter-spacing: 0.04em;
    text-transform: uppercase;

    color: $third-text-color;

    @media screen and (min-width: 1280px) {
        margin-bottom: 32px;

        font-size: 30px;
        line-height: 1.133;
    }
}

.products-description {
    margin-bottom: 30px;
    padding: 0px 40px;

    font-family: $main-text-font, sans-serif;
    font-weight: 700;
    font-size: 14px;
    line-height: 1.52;
    letter-spacing: 0.04em;

    color: $third-text-color;

    @media screen and (min-width: 768px) {
        margin-bottom: 40px;
        padding: 0px 11px;
        line-height: 1.76;
    }

    @media screen and (min-width: 1280px) {
        margin-bottom: 62px;
        padding: 0px 44px;
        font-size: 16px;
    }
}

.products-description-hidden {
    @media screen and (min-width: 768px) {
        display: none;
    }
}

.prodacts-cardset {
    
    @media screen and (min-width: 768px) {
        display: flex;
        margin: 0px -10px;
    }

    @media screen and (min-width: 1280px) {
        margin: 0px -15px;
    }
}

.product-card {
    position: relative;
    padding-top: 161px;
    border-radius: 24px;

    &:not(:last-child) {
        @media screen and (max-width: 767px) {
            margin-bottom: 130px;
        }
    }

    &:nth-child(n + 1) {
        background-color: $product-contant-container-1;
    }

    &:nth-child(n + 2) {
        background-color: $product-contant-container-2;
    }

    &:nth-child(n + 3) {
        background-color: $product-contant-container-3;
    }

    @media screen and (min-width: 768px) {
        width: calc((100% - 6 * 10px) / 3);
        margin: 0px 10px;
        padding-top: 152px;
    }

    @media screen and (min-width: 1280px) {
        width: calc((100% - 6 * 15px) / 3);
        margin: 0px 15px;
        padding-top: 163px;
    }
}

.product-img {
    position: absolute;
    left: 50%;
    top: -113px;
    transform: translateX(-50%);
    width: 249px;

    @media screen and(min-width: 768px) {
        width: 220px;
        top: -104px;
    }

    @media screen and(min-width: 1280px) {
        width: 337px;
        top: -210px;
    }
}

.product-btn {
    display: inline-block;
    margin-bottom: 41px;
    width: 40px;
    height: 40px;
    border-radius: 50%;

    border: none;
    background-color: $button-bg-1;

    transition: background-color 250ms cubic-bezier(0.4, 0, 0.2, 1);

    @media screen and(min-width: 768px) {
        margin-bottom: 40px;
    }

    @media screen and(min-width: 1280px) {
        margin-bottom: 50px;
    }
}

.icon-arrow-right {
    fill: $accent-color;
    transition: fill 250ms cubic-bezier(0.4, 0, 0.2, 1);
}

.product-btn:hover,
.product-btn:focus {
    background-color: $button-bg-2;
}

.product-btn:hover .icon-arrow-right,
.product-btn:focus .icon-arrow-right {
    fill: $button-bg-1;
}

.circles-row {
    display: flex;
    justify-content: center;
    margin-bottom: 30px;

    @media screen and(min-width: 1280px) {
        margin-bottom: 32px;
    }
}

.circle {
    width: 6px;
    height: 6px;
    border-radius: 50%;

    background-color: $button-bg-1;

    &:not(:last-child) {
        margin-right: 4px;
    }
}

sources.sass\\components\\_tradition.scss:
