  padding-top: 43px;
  padding-left: 50px;
  padding-right: 50px;
  padding-bottom: 20px;
  margin-bottom: 30px;

  background-color: #fafafa;
  &::before {
    content: '';
    position: absolute;
    z-index: 0;
    top: 28px;
    left: 61px;
    display: block;
    width: 54px;
    height: 40px;
    background-image: url(./../images/quotes.svg);
  }
  @media screen and(min-width:768px) {
    display: none;
  }
}
.slider-comment-tablet {
  display: none;
  position: relative;
  padding-top: 47px;
  padding-bottom: 28px;
  margin-bottom: 30px;

  background-color: #fafafa;
  &::before {
    content: '';
    position: absolute;
    z-index: 0;
    top: 28px;
    left: 61px;
    display: block;
    width: 54px;
    height: 40px;
    background-image: url(./../images/quotes.svg);
    @media screen and(min-width:768px) {
      top: 30px;
      left: 40px;
    }
  }

  @media screen and(min-width:768px) and (max-width: 1279px) {
    display: block;
  }
}
.slider-comment-desktop {
  display: none;
  position: relative;
  padding-top: 47px;
  padding-bottom: 32px;
  margin-bottom: 30px;

  background-color: #fafafa;
  &::before {
    content: '';
    position: absolute;
    z-index: 0;
    top: 28px;
    left: 61px;
    display: block;
    width: 54px;
    height: 40px;
    background-image: url(./../images/quotes.svg);
