  margin-right: auto;
  margin-bottom: 18px;

  display: flex;
  justify-content: space-between;
  width: 32px;
  height: 4px;
}
.slider-slaid-tot {
  width: 4px;
  height: 4px;

  background-color: #c7133e;
  border-radius: 50%;
}
.slider-client-name {
  font-size: 14px;
  line-height: 1.857;
  text-align: center;
  @media screen and(min-width:768px) {
    font-size: 16px;
    line-height: 1.875;
  }
}
.slider-slaid-image {
  margin-top: 30px;
  margin-left: auto;
  margin-right: auto;
  display: flex;
  justify-content: space-between;
  width: 72px;
}
.slider-slaid-image-dot {
  width: 12px;
  height: 12px;

  background: #d9d9d9;
  border-radius: 50%;
}
.slaid-image-dot-home {
  background-image: url(./../images/home.svg);
  border-radius: 0;
  background-color: transparent;
  background-repeat: no-repeat;
  background-size: contain;
}
.slider-hidden {
  display: none;
}

.slider-slick .slick-dots {
  margin-left: auto;
  margin-right: auto;
  margin-top: 30px;
  display: flex;
  justify-content: space-between;
  font-size: 0;
  width: 72px;
}
.slick-dots button {
  width: 12px;
  height: 12px;
  border: 0;
  border-radius: 50%;
  background-color: #d9d9d9;
  outline: 0;
  overflow: hidden;
  &:hover {
    //   background-color: #9b9797;
    transform: scale(1.4);
  }
  transition-property: transform;
  transition-duration: 250ms;
  transition-timing-function: linear;
}
.slick-active button {
  background-image: url(./../images/home.svg);
  border-radius: 0;
  background-color: transparent;
  background-repeat: no-repeat;
  background-size: contain;
}

