  }
  @media screen and (min-width: 768px) and (max-width: 1279px) {
    display: block;
    margin-bottom: 0;
    max-width: 220px;
    min-height: 544px;
    padding: 50px 31px;
  }

  @media screen and (min-width: 768px) {
    &:not(:last-child) {
      margin-right: 20px;
      margin-bottom: 0;
    }
  }

  @media screen and (min-width: 1280px) {
    max-width: 330px;
    height: auto;
    padding: 51px 44px 62px 43px;
    margin-bottom: 0;
  }

  @media screen and (min-width: 1280px) {
    &:not(:last-child) {
      margin-right: 31px;
    }
  }
}

