  margin-bottom: 25px;
  font-family: DM Sans;
  font-weight: 500;
  font-size: 12px;
  line-height: 1.5;
  color: $second-text-color;

  border-bottom: 1px solid #e1e1e1;

  @media screen and (min-width: 768px) {
    padding-bottom: 30px;
    margin-bottom: 30px;
    font-size: 14px;
    line-height: 1.66;
  }

  @media screen and (min-width: 1280px) {
    padding-bottom: 32px;
