  color: $first-text-color;

  @media screen and (min-width: 768px) {
    font-size: 14px;
    line-height: 1.86;
  }

  @media screen and (min-width: 1280px) {
    font-size: 16px;
    line-height: 1.9;
  }
}
.timetable-list {
  padding-bottom: 25px;
  border-bottom: 1px solid #e1e1e1;

  @media screen and (min-width: 768px) {
    padding-bottom: 30px;
  }

  @media screen and (min-width: 1280px) {
    padding-bottom: 32;
  }
}
.timetable-desc {
  font-family: DM Sans;
  font-weight: 500;
  font-size: 12px;
  line-height: 1.83;

  color: $second-title-color;

  @media screen and (min-width: 768px) {
    font-size: 14px;
    line-height: 1.86;
  }

  @media screen and (min-width: 1280px) {
    font-size: 16px;
    line-height: 1.9;
  }
}
.outlets-contacts {
  margin-top: 25px;

  @media screen and (min-width: 768px) {
    margin-top: 30px;
  }

  @media screen and (min-width: 1280px) {
    margin-top: 32px;
  }
}
.contacts-item {
  font-family: DM Sans;
  font-weight: 500;
  font-size: 12px;
  line-height: 1.5;
  color: $second-text-color;
  text-decoration: none;

  @media screen and (min-width: 768px) {
    font-size: 14px;
    line-height: 1.86;
  }

  @media screen and (min-width: 1280px) {
    font-size: 16px;
    line-height: 1.8;
  }
}
.outlets-time-item:first-child {
  margin-bottom: 10px;
  @media screen and(min-width:768px) {
    margin-bottom: 15px;
  }
}
.outlets-nav {
  display: flex;
  justify-content: center;

  &:not(:last-child) {
    margin-right: 15px;
  }
  @media screen and (max-width: 767px) {
    flex-direction: column;
    align-items: center;
  }
}
.outlets-nav .btn {
  padding: 0;
  padding-top: 12px;
  padding-bottom: 12px;
  display: block;

  font-weight: 700;
  font-size: 16px;
  line-height: 1.3125;

  color: $button-bg-2;
  background-color: $button-bg-1;
  border: 0;
  border-radius: 22px;
  transition: background-color 250ms cubic-bezier(0.4, 0, 0.2, 1);
  &:hover,
  &:focus {
    background-color: $button-hover-1;
  }
}
.outlets-red {
  margin-bottom: 10px;
  max-width: 161px;
  width: 100%;
  @media screen and (min-width: 768px) {
    margin: 0 15px 0 0;
    max-width: 191px;
    width: 100%;
  }
}
.outlets-white {
  width: 100%;
  max-width: 161px;
  @media screen and (min-width: 768px) {
    max-width: 174px;
    width: 100%;
  }
}

button {
  outline: transparent;
}

.btn.outlets-red {
  background-color: $button-bg-2;
  color: $button-bg-1;
}
.outlets-btn-icon {
  margin-left: 8px;
  width: 7px;
  height: 10px;
  stroke: $button-bg-2;
}
.outlets-btn-icon-white {
  margin-left: 8px;
  width: 7px;
  height: 10px;
  stroke: $button-bg-1;
}

sources.sass\\components\\_footer.scss:
.footer {
  background-color: $second-bg-color;
}
.footer-content {
  border-bottom: 1px solid $main-bg-color;

  @media screen and (min-width: 768px) {
    display: flex;
    padding-bottom: 91px;
  }
  @media screen and (max-width: 767px) {
    padding-bottom: 97px;
  }
  @media screen and (min-width: 1280px) {
    display: flex;
    justify-content: space-between;
  }
}
.footer-text {
  @media screen and (max-width: 767px) {
    width: 234px;
    margin-bottom: 43px;
  }
  @media screen and (min-width: 768px) {
    margin-right: 92px;
    width: 340px;
  }

  @media screen and (min-width: 1280px) {
    margin-right: 257px;
    width: 450px;
  }
}

.toptext {
  font-family: Titan One;
  font-style: normal;
  font-weight: normal;
  font-size: 18px;
  line-height: 1.166;
  text-transform: uppercase;
  color: $second-title-color;

  @media screen and (max-width: 767px) {
    margin-bottom: 17px;
  }
  @media screen and (min-width: 768px) {
    margin-bottom: 15px;
    font-size: 26px;
    line-height: 1.153;
  }
  @media screen and (min-width: 1280px) {
    margin-bottom: 15px;
    font-size: 28px;
    line-height: 1.133;
  }
}
.lowertext {
  font-family: Titan One;
  font-style: normal;
  font-weight: normal;
  font-size: 34px;
  line-height: 1.147;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  color: $first-title-color;

  @media screen and (min-width: 768px) {
    font-size: 48px;
    line-height: 1.145;
  }
  @media screen and (min-width: 1280px) {
    font-size: 54px;
    line-height: 1.137;
  }
}
.footer-contacts {
  @media screen and (max-width: 767px) {
    padding-bottom: 43px;
  }
}
.contacts-icon__list {
  display: flex;
  justify-content: flex-end;

  margin-bottom: 46px;
}
.contacts-icon__item {
  margin-right: 28px;
  &:last-child {
    margin-right: 0;
  }
  display: flex;
  justify-content: center;
  align-items: center;

  border-radius: 50%;
  width: 32px;
  height: 32px;
  background-color: $second-title-color;
  transition: all 250ms ease;
  &:hover {
    background-color: #d41443;
  }
}
.icon-link {
  fill: $button-bg-1;
  padding-top: 2px;
  &_telegram {
    margin-left: -3px;
  }
}
.contacts-tel {
  font-family: DM Sans;
  font-style: normal;
  font-weight: bold;
  font-size: 12px;
  line-height: 1.86;
  text-align: right;
}
.tel-number {
  color: $first-title-color;
  text-decoration: none;
  margin-left: 1px;
}
.copyright {
  text-align: center;
}
.copyright-text {
  padding-top: 22px;
  padding-bottom: 23px;

  font-family: DM Sans;
  font-style: normal;
  font-weight: bold;
  font-size: 14px;
  line-height: 1.52;
  color: $second-text-color;

  @media screen and (max-width: 767px) {
    white-space: pre-line;
  }
  @media screen and (min-width: 768px) {
    padding-top: 29px;
    padding-bottom: 18px;
  }
  @media screen and (min-width: 1280px) {
    padding-top: 9px;
    padding-bottom: 10px;
  }
}
